import React, { useRef } from "react";
import {
  View,
  Text,
  StyleSheet,
  Image,
  Dimensions,
  TouchableOpacity,
} from "react-native";
import Carousel from "react-native-snap-carousel";

const { width: screenWidth, height: screenHeight } = Dimensions.get("window");

const data = [
  {
    title: "Welcome to The Canadian Citizenship Test",
    subtitle: "No ads, just learning.",
    text: "Study the latest material, practice using quizzes, test your knowledge using tests",
    image: require("../../assets/leaf.png"),
  },
  {
    title: "Ad-Free Experience",
    text: "This is the second page.",
    image: require("../../assets/secondslider.png"),
  },
  {
    title: "Unlimited Mock Tests",
    subtitle: "For Premium Users",
    text: "As a premium user, access unlimited mock tests to thoroughly prepare for the Canadian citizenship exam.",
    image: require("../../assets/leaf.png"),
  },
];

const CarouselScreen = () => {
  const carouselRef = useRef(null);

  const renderItem = ({ item, index }) => (
    <View style={styles.item}>
      <Image source={item.image} style={styles.image} />
      <Text style={styles.title}>{item.title}</Text>
      <Text style={styles.subtitle}>{item.subtitle}</Text>
      <Text style={styles.text}>{item.text}</Text>
      <View style={styles.pagination}>
        {data.map((_, i) => (
          <View
            key={i}
            style={[
              styles.dot,
              i === index ? styles.activeDot : styles.inactiveDot,
            ]}
          />
        ))}
      </View>
      {index <= data.length - 3 && (
        <TouchableOpacity
          style={styles.button}
          onPress={() => carouselRef.current.snapToNext()}
        >
          <Text style={styles.buttonText}>NEXT ➔</Text>
        </TouchableOpacity>
      )}
      {index == data.length - 2 && (
        <TouchableOpacity
          style={styles.button}
          onPress={() => carouselRef.current.snapToNext()}
        >
          <Text style={styles.buttonText}>Get Started ➔</Text>
        </TouchableOpacity>
      )}
    </View>
  );

  return (
    <Carousel
      ref={carouselRef}
      data={data}
      renderItem={renderItem}
      sliderWidth={screenWidth}
      itemWidth={screenWidth * 0.8}
      layout="default"
    />
  );
};

const styles = StyleSheet.create({
  item: {
    backgroundColor: "white",
    borderRadius: 10,
    height: 400,
    paddingTop: 50,
    paddingHorizontal: 20,
    justifyContent: "center",
    alignItems: "center",
  },
  image: {
    width: 150,
    height: 150,
    resizeMode: "contain",
    marginBottom: 30,
  },
  title: {
    fontSize: 12,
    fontWeight: "bold",
    textAlign: "center",
    marginBottom: 10,
    color: "#FB3640",
  },
  subtitle: {
    fontSize: 10,
    textAlign: "center",
    marginBottom: 20,
  },
  text: {
    fontSize: 10,
    textAlign: "center",
    marginBottom: 20,
  },
  pagination: {
    flexDirection: "row",
    justifyContent: "center",
    marginBottom: 20,
  },
  dot: {
    width: 10,
    height: 10,
    borderRadius: 5,
    marginHorizontal: 5,
  },
  activeDot: {
    backgroundColor: "black",
  },
  inactiveDot: {
    backgroundColor: "lightgray",
  },
  button: {
    backgroundColor: "#FB3640",
    borderRadius: 20,
    paddingVertical: 10,
    paddingHorizontal: 20,
  },
  buttonText: {
    color: "white",
    fontSize: 8,
    fontWeight: "bold",
  },
});

export default CarouselScreen;
